mapper.select:
SELECT external
FROM lms_takes join lms_attendanceCheck on lms_takes.studentID = lms_attendanceCheck.studentID
WHERE classID = #{classID} and lms_takes.studentID = #{studentID}

select count(distinct attendanceID) 
from lms_attendanceCheck join lms_attendance on lms_attendanceCheck.attendanceID = lms_attendance.id
where lms_attendance.classID = #{classID}
