mapper.update:
update videoCheck
set lastTime=#{lastTime}, timer=#{timer}
where studentID=#{studentID} AND videoID=#{videoID}

update videoCheck
set lastTime=#{lastTime}, timer=#{timer}, watched=#{watched}
where studentID=#{studentID} AND videoID=#{videoID}

mapper.select:
select *
from videoCheck
where studentID=#{studentID} AND videoID=#{videoID}
