mapper.update:
update lms_playlist
set playlistName=#{playlistName}, description=#{description}, tag=#{tag}, exposed=#{exposed} 
where id=#{id}

mapper.insert:
update lms_playlist 
set thumbnailID= (SELECT youtubeID FROM lms_video WHERE playlistID=#{playlistID} order by seq limit 0,1) 
where id=#{playlistID}

mapper.select:

select * from lms_playlist 
where instructorID=#{instructorID}
order by seq desc
